{ "tiddlers": { "$:/copy": { "text": "<span id={{!!title}}><$transclude tiddler={{!!title}}/></span> <span class='tc-copy-button'><$button param={{!!title}} message='tm-copy' tooltip={{$:/language/Buttons/Copy/Hint}} aria-label={{$:/language/Buttons/Copy/Caption}} class='tc-btn-invisible'><$action-sendmessage $message='tm-notify' $param='$:/language/Buttons/Copy/Notification'/>{{$:/core/images/copy-clipboard}}</$button></span>", "title": "$:/copy" }, "$:/language/Buttons/Copy/Caption": { "text": "copy", "title": "$:/language/Buttons/Copy/Caption" }, "$:/language/Buttons/Copy/Hint": { "text": "Copy to the clipboard", "title": "$:/language/Buttons/Copy/Hint" }, "$:/language/Buttons/Copy/Notification": { "text": "Copied", "title": "$:/language/Buttons/Copy/Notification" }, "$:/pluginlibrary/ajh/released": { "text": "The plugin library for the latest and greatest plugins from [ext[t5a|https://github.com/t5a/t5a.github.io.git]]; most recent released versions of plugins by [ext[Andrew|http://t5a.github.io/#Andrew%20Harrison]].", "url": "https://t5a.github.io/plugins", "title": "$:/pluginlibrary/ajh/released", "tags": "$:/tags/PluginLibrary", "caption": "Tiddlywiki Library for Plugins by Andrew Harrison" }, "$:/plugins/ajh/copy/copy.js": { "text": "/*\
$:/plugins/ajh/copy/copy.js
type: application/javascript
module-type: startup

Setup the root widget handler that can be used to copy transcluded tiddler to the Clipboard.

<span id='TiddlerTitle'><$transclude tiddler='TiddlerTitle'/></span>&nbsp;<$button param='TiddlerTitle' message='tm-copy'>COPY</$button>

\*/
(function () {

/*jslint node: true, browser: true */
/*global $tw: false */
'use strict';

// Export name and synchronous status
exports.name = 'copy';
exports.platforms = ['browser'];
exports.after = ['rootwidget'];
exports.synchronous = true;

exports.startup = function () {
 // Install the tiddler copy message mechanism
 $tw.rootWidget.addEventListener('tm-copy', function (event) {
 if (event) { 
 try {
 var copyDiv = document.getElementById(event.param);
 copyDiv.contentEditable = true;
 copyDiv.focus();
 document.execCommand('SelectAll',false,null);
 document.execCommand('Copy', false, null);
 document.execCommand('Unselect');
 copyDiv.blur();
 return;
 } catch(e) {
 // Rethrow exception if it's not a SecurityError. Note that SecurityError
 // exception is specific to Firefox.
 if(e.name !== 'SecurityError')
 throw e;
 alert('Feature not available in older browsers!');
 return;
 }
 }
 });
};

})();", "type": "application/javascript", "title": "$:/plugins/ajh/copy/copy.js", "module-type": "startup" }, "$:/plugins/ajh/copy/icon": { "text": "<svg class='tc-image-copy tc-image-button' width='22pt' height='22pt' viewBox='0 0 192 192'><path fill='#0ff' stroke='#30f' stroke-width='7.5' d='m13.5,52.2l82.5,-48l82.5,48v87.3l-82.5,48l-82.5,-48v-87.3z'/><g fill='#000'>
