{
    "tiddlers": {
        "$:/language/Buttons/OpenAll/Caption": {
            "text": "Open All",
            "title": "$:/language/Buttons/OpenAll/Caption"
        },
        "$:/language/Buttons/OpenAll/Hint": {
            "text": "Open all by tag",
            "title": "$:/language/Buttons/OpenAll/Hint"
        },
        "$:/plugins/ajh/openall/icon": {
            "text": "\define my-svg()
<svg class='tc-image-openall tc-image-button' width='22pt' height='22pt' viewBox='0 0 192 192'><path fill='#30f' stroke='#f00' stroke-width='7.5' d='m13.5,52.2l82.5,-48l82.5,48v87.3l-82.5,48l-82.5,-48v-87.3z'/><path fill='#fff' stroke='#30f' stroke-width='5' d='m$(m)$$(path)$'/></g></svg>
\end

<$vars m='96,14' path={{$:/plugins/ajh/openall/image!!path}}>
<<my-svg>>
</$vars>",
            "title": "$:/plugins/ajh/openall/icon",
            "tags": "$:/tags/Image"
        },
        "$:/plugins/ajh/openall/image": {
            "text": "\define my-svg()
<svg class='tc-image-openall tc-image-button' width='22pt' height='22pt' viewBox='0 0 128 128'><path d='m$(m)$$(path)$'/></svg>
\end

<$vars m='64,3' path={{!!path}}>
<<my-svg>>
</$vars>",
            "title": "$:/plugins/ajh/openall/image",
            "tags": "$:/tags/Image",
            "path": "l64,73h-128l64,-73zm-64,104h128v24h-128v-24z"
        },
        "$:/plugins/ajh/openall/readme": {
            "text": "This plugin adds the {{$:/plugins/ajh/openall/template}} button to the tag manager drop down menu using the{{$:/tags/TagDropdown||$:/core/ui/TagTemplate}}system tag. The open all button opens all non system tiddlers tagged with that drop down tag.

Simply drag $:/plugins/ajh/openall to the top of your tiddlywiki display, save and reload.

This plugin comprises:

* Icon
* Template
* Stylesheet
* Caption and hint",
            "title": "$:/plugins/ajh/openall/readme"
        },
        "$:/plugins/ajh/openall/stylesheet": {
            "text": ".tc-sidebar-lists button.openall { color: #999; }
.tc-sidebar-lists button.openall:hover { color: #444; }
.tc-sidebar-lists .tc-drop-down button svg, .tc-drop-down a svg { fill: #999; }
.tc-sidebar-lists .tc-drop-down button.tc-btn-invisible:hover svg { fill: #444; }
.tc-drop-down button.tc-btn-invisible:hover svg { fill: #fff; }",
            "title": "$:/plugins/ajh/openall/stylesheet",
            "tags": "$:/tags/Stylesheet"
        },
        "$:/plugins/ajh/openall/template": {
            "text": "\define lingo-base() $:/language/Buttons/

<$button tooltip={{$:/language/Buttons/OpenAll/Hint}} aria-label={{$:/language/Buttons/OpenAll/Caption}} class='tc-btn-invisible openall'>
<$list filter='[tag{!!title}]'>
<$action-navigate $to=<<currentTiddler>> />
</$list>
<$list filter='[<tv-config-toolbar-icons>prefix[yes]]'>
{{$:/plugins/ajh/openall/image}}
</$list>
<$list filter='[<tv-config-toolbar-text>prefix[yes]]'>
<<lingo OpenAll/Caption>>
</$list>
</$button>",
            "title": "$:/plugins/ajh/openall/template",
            "tags": "$:/tags/TagDropdown"
        }
    }
}
