{
    "tiddlers": {
        "$:/plugins/ajh/tiddlyviewer": {
            "core-version": ">=5.1.11",
            "created": "20150814132714911",
            "dependents": "$:/plugins/ajh/tiddlyviewer/icon $:/plugins/ajh/tiddlyviewer/readme $:/plugins/ajh/tiddlyviewer/stylesheet $:/plugins/ajh/tiddlyviewer/template",
            "description": "Tiddly Viewer",
            "list": "readme",
            "modified": "20170527235951006",
            "plugin-type": "plugin",
            "source": "http://tiddlyviewer.tiddlyspot.com/",
            "title": "$:/plugins/ajh/tiddlyviewer",
            "type": "application/json",
            "version": "0.0.14",
            "text": "{
    "tiddlers": {
        "$:/plugins/ajh/tiddlyviewer/readme": {
            "text": "This plugin displays external pages fully formatted instead of as just plain text. By adding a ''tiddly'' field and path to an external page, it recognizes to display the page in an object frame.\n\n<mark>@@color:red; ''WARNING'' @@</mark>: Use of Tiddly Viewer to display untrusted web sites could be a security risk.\n\nSimply drag $:/plugins/ajh/tiddlyviewer to the top of your tiddlywiki display, save and reload.\n\nThis plugin comprises:\n\n* Template\n* Stylesheet",
            "title": "$:/plugins/ajh/tiddlyviewer/readme"
        },
        "$:/plugins/ajh/tiddlyviewer/stylesheet": {
            "text": "/* EXPAND */\n.tc-tiddly-viewer {\n    width: 100%;\n    height: 85vh;\n}",
            "title": "$:/plugins/ajh/tiddlyviewer/stylesheet",
            "tags": "$:/tags/Stylesheet"
        },
        "$:/plugins/ajh/tiddlyviewer/template": {
            "text": "<$reveal type='nomatch' state=<<folded-state>> text='hide' retain='yes' animate='yes'>\n<$list filter='[all[current]has[tiddly]]'>\n<object type='text/html' data={{!!tiddly}} class='tc-tiddly-viewer'></object>\n</$list>\n</$reveal>",
            "title": "$:/plugins/ajh/tiddlyviewer/template",
            "tags": "$:/tags/ViewTemplate",
            "list-after": "$:/core/ui/ViewTemplate/tags"
        },
        "$:/plugins/ajh/tiddlyviewer/icon": {
            "text": "<svg class='tc-image-tiddlyviewer ic-image-button' width='22pt' height='22pt' viewBox='0 0 128 128'><path fill='#ff0' stroke='#30f' stroke-width='5' d='m9,34.7l55,-32l55,32v58.2l-55,32l-55,-32v-58.2z'/><path fill='#30f' d='m48,44c-17.7,5.2 -31.4,15.6 -27.5,20.7c1.7,2.3 3.1,2.1 7.1,-1.2c4.1,-3.4 16.7,-10.2 18.9,-10.2c1.2,0 1.4,1.1 1.2,5.1c-0.6,9.3 5.9,16.8 15,17.4c4.2,0.3 6.2,-0.1 9.3,-2.1c5.2,-3.3 8.8,-10.7 8,-16.5c-0.4,-3.1 -0.2,-4 0.9,-4c2.4,0 10.2,4 16.8,8.6c6.8,4.9 8,5.2 9.8,2.8c2.7,-3.6 -1.8,-8.6 -13.8,-15.6c-12.2,-6.6 -33.2,-9.3 -45.7,-5zm-13,32.7c-0.9,2.7 0.3,3.8 7.1,6.1c10.7,3.6 21.7,4.8 31.4,3.4c10.9,-1.5 21.5,-5.5 21.9,-8c0.6,-3.2 -2.5,-3.4 -10.1,-0.8c-10.9,3.8 -25.8,4.3 -36.8,1.1c-13,-3.7 -12.8,-3.7 -13.5,-1.8z'/></svg>",
            "title": "$:/plugins/ajh/tiddlyviewer/icon",
            "tags": "$:/tags/Image"
        }
    }
}"
        }
    }
}
