{ "tiddlers": { "$:/language/toplefttoolbar/caption": { "text": "Top Left Toolbar", "title": "$:/language/toplefttoolbar/caption" },
"$:/language/toplefttoolbar/hint": { "text": "Choose which buttons are displayed on the top left toolbar. Drag and drop selected to change the ordering", "title": "$:/language/toplefttoolbar/hint" },
"$:/plugins/ajh/toplefttoolbar/controls": { "text": "\define lingo-base() $:/language/toplefttoolbar/
\define add() [[$(currentTiddler)$]]
\define remove() -[[$(currentTiddler)$]]
\define drop-actions()
<$action-listops $tiddler='$:/plugins/ajh/toplefttoolbar/list' $subfilter='+[insertbefore:currentTiddler<actionTiddler>]'/>
\end

<<lingo hint>>

<$set name='tv-config-toolbar-icons' value='yes'>

<$set name='tv-config-toolbar-text' value='yes'>

<$macrocall $name='list-links-draggable' tiddler='$:/plugins/ajh/toplefttoolbar/list' itemTemplate='$:/plugins/ajh/toplefttoolbar/template' class='tc-topbar-nobull'/>
<$list filter='[all[shadows+tiddlers]tag[$:/tags/PageControls]!has[draft.of]]'>
<$list filter='[all[current]!list[$:/plugins/ajh/toplefttoolbar/list]]'>
<div>
<$button class=<<tv-config-toolbar-class>>>
<$action-listops $tiddler='$:/plugins/ajh/toplefttoolbar/list' $field='list' $subfilter=<<add>>/>
<input type='checkbox' unchecked='true'/>
</$button>
<span class="tc-icon-wrapper">
<$transclude tiddler=<<currentTiddler>> field='caption'/>
<i class='tc-muted'>
 -- <$transclude tiddler=<<currentTiddler>> field='description'/>
</i>
</span>
</div>
</$list>
</$list>

</$set>

</$set>", "title": "$:/plugins/ajh/toplefttoolbar/controls", "tags": "$:/tags/ControlPanel/Toolbars", "caption": "{{$:/language/toplefttoolbar/caption}}" },
"$:/plugins/ajh/toplefttoolbar/icon": { "text": "<svg class='tc-image-toplefttoolbar ic-image-button' width='22pt' height='22pt' viewBox='0 0 128 128'><path fill='#c0f' stroke='#000' stroke-width='5' d='m9,34.7l55,-32l55,32v58.2l-55,32l-55,-32v-58.2z'/><circle fill='#fff' cx='38' cy='64' r='7'/><circle fill='#fff' cx='64' cy='64' r='7'/><circle fill='#fff' cx='90' cy='64' r='7'/></svg>", "title": "$:/plugins/ajh/toplefttoolbar/icon", "tags": "$:/tags/Image" },
"$:/plugins/ajh/toplefttoolbar/readme": { "text": "This plugin adds the Top Left Toolbar to the Toolbars tab in the control panel. Drag and drop selected to change the ordering. It provides a list of buttons that have the $:/tags/PageControls tag and can be selected to appear in the Top left toolbar.<br>

Simply drag $:/plugins/ajh/toplefttoolbar to the top of your tiddlywiki display, save and reload.

This plugin comprises:

* Control settings
* Template
* Stylesheet
* Toolbar
* Caption and hint", "title": "$:/plugins/ajh/toplefttoolbar/readme" },
"$:/plugins/ajh/toplefttoolbar/stylesheet": { "text": "/* PREVENT OVERLAPPING TOPRIGHTBAR ON MOBILE PHONES */
.tc-topbar-left {
 margin-right: {{!!right}};
}

/* MARGINS AND SIZE AND COLOR */
.tc-topbar-left .tc-page-controls {
 margin-top: 0;
 font-size: {{!!button-size}};
}
.tc-topbar .tc-page-controls button {
 margin-right: 0em;
}
.tc-topbar-left .tc-page-controls button svg {
 margin-top: {{!!top}};
 fill: {{!!button-color}};
}
.tc-topbar-left button:hover svg {
 fill:{{!!button-hover-color}} !important;
}
.tc-topbar .tc-btn-text {
 color: {{!!text-color}};
}
.tc-topbar .tc-btn-text:hover {
 color: {{!!text-hover-color}};
}

/* CONTROL PANEL LIST */
.tc-topbar-nobull {
 list-style-type: none;
 -webkit-margin-after: -1.5em;
 -webkit-padding-start: 0px;
}
.tc-topbar-nobull a.tc-tiddlylink-shadow {
 font-weight: normal;
}", "top": "inherit", "title": "$:/plugins/ajh/toplefttoolbar/stylesheet", "text-hover-color": "", "text-color": "", "tags": "$:/tags/Stylesheet", "right": "3em", "button-size": "inherit", "button-hover-color": "", "button-color": "", "caption": "Top Left Toolbar" },
"$:/plugins/ajh/toplefttoolbar/template": { "text": "<$draggable tiddler=<<currentTiddler>>>
<$button class=<<tv-config-toolbar-class>>>
<$action-listops $tiddler='$:/plugins/ajh/toplefttoolbar/list' $field='list' $subfilter=<<remove>>/>
<input type='checkbox' checked='checked'>
</$button> <span class='tc-icon-wrapper'> <$transclude field='caption'/> <i class='tc-muted'>-- <$transclude field='description'/></i></span>
</$draggable>", "title": "$:/plugins/ajh/toplefttoolbar/template" },
"$:/plugins/ajh/toplefttoolbar/toolbar": { "text": "<div class='tc-page-controls'>
<$list filter='[list[$:/plugins/ajh/toplefttoolbar/list]]'><$transclude field='text' mode='inline'/>
</$list>
</div>", "title": "$:/plugins/ajh/toplefttoolbar/toolbar", "tags": "$:/tags/TopLeftBar" } } }
